#!/usr/bin/env node

var argv = require('yargs').argv,
  ndm = require('ndm')('ndm-test');

if (argv._[0] === 'install') ndm.install();
else if (argv._[0] === 'remove') ndm.remove();
else if (argv._[0] === 'start') ndm.start();
else if (argv._[0] === 'restart') ndm.restart;
else if (argv._[0] === 'stop') ndm.stop();
